// Get USDtoBRL price from uol

module.exports = function (resolve) {
    var price = require('child_process').spawn('sh', ['-c', 'curl --silent "http://economia.uol.com.br/cotacoes/"']);

    var final = '';
    
    price.stdout.on('data', function (data) {
        final += data.toString();
    });

    price.stdout.on('end', function () {
        var start = final.indexOf("R$ ");
        var end = final.substr(start+3).indexOf(" ");
        
        var price = parseFloat(final.substr(start+3, end).replace(",", "."), 10);
        
        resolve({usdBRL:price});
    });
};